mapper.select:
SELECT * FROM marketBoard WHERE nickname NOT
IN(#{nickname}) AND address2 LIKE #{address} ORDER BY status DESC, market_seq DESC
LIMIT 3;

mapper.update:
UPDATE marketBoard SET buyer = #{buyer}, sell_date =
now() at time zone 'Asia/Seoul', status = 0 WHERE market_seq =
#{buy_seq}

mapper.select:
SELECT a.*, b.content FROM 
(SELECT * FROM marketboard WHERE buyer = #{nickname} ORDER BY sell_date DESC) AS a JOIN
(SELECT * FROM marketrating) AS b ON a.market_seq = b.market_seq;
