mapper.select:
SELECT count(title) FROM (SELECT * FROM community 


			WHERE title LIKE #{keyword} 


) AS a WHERE category = #{kind} AND address2 LIKE #{address}

SELECT * FROM (SELECT (row_number() over()) AS rnum, * FROM (SELECT * FROM (SELECT * FROM community 


		WHERE title LIKE #{keyword} 


		WHERE content LIKE #{keyword}


) AS a WHERE category = #{kind} AND address2 LIKE #{address} ORDER BY community_seq DESC) AS c) AS b WHERE rnum >= (#{page} * 9 - 8) AND rnum <= (#{page} * 9)

SELECT a.*, b.profile FROM (SELECT * FROM community WHERE community_seq = #{seq}) AS a
LEFT JOIN 
(SELECT * FROM member) AS b ON a.nickname = b.nickname;
