mapper.select:
select profile from member


		where nickname = #{recv_nick}


		where nickname = #{send_nick}

select m.no, m.room, m.send_nick, m.recv_nick, m.send_time, m.read_time, m.content, m.image, m.read_check, b.profile from message m left outer join member b on m.send_nick = b.nickname
where room=#{room}
where (recv_nick = #{recv_nick} and send_nick = #{nickname}) or (send_nick = #{recv_nick} and recv_nick = #{nickname})

mapper.update:
update message set read_check=1, read_time = now() at time zone 'Asia/Seoul'


		where room=#{room} and read_check=0 and recv_nick=#{nickname}


		where send_nick=#{recv_nick} and read_check=0 and recv_nick=#{nickname}
